import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { getDogDetail } from "../../redux/actions/index";
import "./dogDetails.css";

const DogDetails = ({ match }) => {
const dispatch = useDispatch();
const dog = useSelector((state) => state.dogDetail);

useEffect(() => {
dispatch(getDogDetail(match.params.id));
}, [dispatch, match.params.id]);

return (
<div className="dog-details">
{dog ? (
<>
<h1>{dog.name}</h1>
<div className="dog-image">
<img src={dog.image.url} alt={dog.name} />
</div>
<div className="dog-details-info">
<p>
<span>Temperament:</span> {dog.temperament}
</p>
<p>
<span>Height:</span> {dog.height.metric} cm
</p>
<p>
<span>Weight:</span> {dog.weight.metric} kg
</p>
<p>
<span>Life Span:</span> {dog.life_span}
</p>
<p>
<span>Bred for:</span> {dog.bred_for}
</p>
<p>
<span>Breed group:</span> {dog.breed_group}
</p>
<p>
<span>Origin:</span> {dog.origin}
</p>
</div>
</>
) : (
<p>Loading...</p>
)}
</div>
);
};

export default DogDetails;
