import React, { useEffect, useState } from "react";
import DogCard from "../dogCard/DogCard";
import Pagination from "../pagination/Pagination";
import { useSelector } from "react-redux";

const DogsList = () => {
  const breeds = useSelector((state) => state.breeds);
  const currentPage = useSelector((state) => state.currentPage);
  const breedsPerPage = useSelector((state) => state.breedsPerPage);

  const [currentBreeds, setCurrentBreeds] = useState([]);

  useEffect(() => {
    const indexOfLastBreed = currentPage * breedsPerPage;
    const indexOfFirstBreed = indexOfLastBreed - breedsPerPage;
    setCurrentBreeds(breeds.slice(indexOfFirstBreed, indexOfLastBreed));
  }, []);

  return (
    <div className="dogslist-container">
      <div className="dogcard-container">
        {breeds.map((breed) => (
          <DogCard key={breed.id} breed={breed} />
        ))}
      </div>

      <div className="pagination">
        <Pagination
          breedsPerPage={breedsPerPage}
          totalBreeds={breeds.length}
          currentPage={currentPage}
        />
      </div>
    </div>
  );
};

export default DogsList;
