import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { changePage, clearSearch, findBreedsByName } from "../../redux/actions";
import DogCard from "../dogCard/DogCard";
import Pagination from "../pagination/Pagination";

const DogsList = () => {
  const dispatch = useDispatch();
  const breeds = useSelector((state) => state.filteredBreeds);
  const currentPage = useSelector((state) => state.currentPage);
  const itemsPerPage = useSelector((state) => state.breedsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = breeds.slice(indexOfFirstItem, indexOfLastItem);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    dispatch(clearSearch());
    dispatch(changePage(1));
  }, [dispatch]);

  const handleSearch = () => {
    const results = breeds.filter(
      (breed) => breed.name.toLowerCase() === searchTerm.toLowerCase()
    );
    setSearchResults(results);
  };

  const handleClear = () => {
    setSearchTerm("");
    setSearchResults([]);
    dispatch(clearSearch());
    dispatch(changePage(1));
  };

  return (
    <>
      <div className="search-bar-container">
        <input
          type="text"
          placeholder="Search by breed name"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {searchTerm && (
          <button className="clear-button" onClick={handleClear}>
            X
          </button>
        )}
        <button className="search-button" onClick={handleSearch}>
          Search
        </button>
      </div>
      {searchResults.length > 0 ? (
        <div className="breeds-container">
          {searchResults.map((breed) => (
            <DogCard key={breed.id} breed={breed} />
          ))}
        </div>
      ) : (
        <div className="breeds-container">
          {currentItems.map((breed) => (
            <DogCard key={breed.id} breed={breed} />
          ))}
          {currentItems.length === 0 && <p>No results found</p>}
        </div>
      )}
      <Pagination itemsPerPage={itemsPerPage} totalItems={breeds.length} />
    </>
  );
};

export default DogsList;
