import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { sortBreedsByName, sortBreedsByWeight } from "../../redux/actions";

const Sorting = () => {
  const dispatch = useDispatch();
  const sortOrder = useSelector((state) => state.sortOrder);

  const handleSortChange = (event) => {
    const selectedSortOrder = event.target.value;
    if (selectedSortOrder === "name") {
      dispatch(sortBreedsByName());
    } else if (selectedSortOrder === "weight") {
      dispatch(sortBreedsByWeight());
    }
  };

  return (
    <div className="sorting">
      <label htmlFor="sort-by">Sort by:</label>
      <select id="sort-by" onChange={handleSortChange} value={sortOrder}>
        <option value="">Select an option</option>
        <option value="name">Name</option>
        <option value="weight">Weight</option>
      </select>
    </div>
  );
};

export default Sorting;
