import React, { useState } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { searchDogsByName, loadDogs } from "../../redux/actions/index";
import "./searchBar.css"

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const dispatch = useDispatch();
  const dogs = useSelector(state => state.dogs);

  const handleSearch = (e) => {
    e.preventDefault();
    // Dispatch the searchDogsByName action with the search term
    dispatch(searchDogsByName(searchTerm));
    // Reset the search term
    setSearchTerm('');
  };

  const handleLoadAll = () => {
    // Dispatch the loadDogs action to load all dogs
    dispatch(loadDogs());
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>
      <button onClick={handleLoadAll}>Load All</button>
    </div>
  );
};

export default SearchBar;
