import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loadDogs, filterDogs, sortDogs, setSort, searchDogsByName, changePage } from '../actions';
import DogsList from './DogsList';
import SearchBar from './SearchBar';
import Filters from './Filters';
import SortBy from './SortBy';
import Pagination from './Pagination';

const HomePage = () => {
  const dispatch = useDispatch();

  const dogs = useSelector(state => state.dogs);
  const loading = useSelector(state => state.loading);
  const error = useSelector(state => state.error);
  const filteredDogs = useSelector(state => state.filteredDogs);
  const searchName = useSelector(state => state.searchName);
  const sortType = useSelector(state => state.sortType);
  const currentPage = useSelector(state => state.currentPage);
  const dogsPerPage = useSelector(state => state.dogsPerPage);
  const totalDogs = filteredDogs.length;

  useEffect(() => {
    dispatch(loadDogs());
  }, [dispatch]);

  const handleSearchByName = (name) => {
    dispatch(searchDogsByName(name));
    dispatch(setSort(''));
    dispatch(changePage(1));
  }

  const handleFilter = (filterObj) => {
    dispatch(filterDogs(filterObj));
    dispatch(setSort(''));
    dispatch(changePage(1));
  }

  const handleSort = (type) => {
    dispatch(sortDogs(filteredDogs, type));
    dispatch(setSort(type));
    dispatch(changePage(1));
  }

  const handlePageChange = (pageNumber) => {
    dispatch(changePage(pageNumber));
  }

  const indexOfLastDog = currentPage * dogsPerPage;
  const indexOfFirstDog = indexOfLastDog - dogsPerPage;
  const currentDogs = filteredDogs.slice(indexOfFirstDog, indexOfLastDog);

  return (
    <div className="home-page">
      <div className="search-container">
        <SearchBar handleSearch={handleSearchByName} />
      </div>
      <div className="filter-container">
        <Filters handleFilter={handleFilter} />
      </div>
      <div className="sort-container">
        <SortBy handleSort={handleSort} sortType={sortType} />
      </div>
      <div className="dogs-container">
        {loading && <p>Loading...</p>}
        {error && <p>{error}</p>}
        {!loading && !error && <DogsList dogs={currentDogs} />}
      </div>
      <div className="pagination-container">
        {totalDogs > 0 &&
          <Pagination
            currentPage={currentPage}
            dogsPerPage={dogsPerPage}
            totalDogs={totalDogs}
            handlePageChange={handlePageChange}
          />
        }
      </div>
    </div>
  );
}

export default HomePage;
