import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getDogs, getTemperaments, setPageNumber, filterByName, filterByTemperament } from "../../redux/actions";
import DogCard from "../dogCard/DogCard";
import SearchBar from "../searchBar/SearchBar";
import FilterDropDown from "../filterDropDown/FilterDropDown";
import Pagination from "../pagination/Pagination";
import "./homePage.css";

const HomePage = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getDogs());
    dispatch(getTemperaments());
    dispatch(setPageNumber(1));
  }, [dispatch]);

  const dogs = useSelector((state) => state.dogs);
  const filteredDogs = useSelector((state) => state.filteredDogs);
  const temperaments = useSelector((state) => state.temperaments);
  const pageNumber = useSelector((state) => state.pageNumber);
  const dogsPerPage = useSelector((state) => state.dogsPerPage);

  const indexOfLastDog = pageNumber * dogsPerPage;
  const indexOfFirstDog = indexOfLastDog - dogsPerPage;
  const currentDogs = filteredDogs.length > 0 ? filteredDogs : dogs.slice(indexOfFirstDog, indexOfLastDog);
  const totalDogs = filteredDogs.length > 0 ? filteredDogs.length : dogs.length;

  const paginate = (pageNumber) => dispatch(setPageNumber(pageNumber));

  const handleSearch = (name) => {
    dispatch(filterByName(name));
    dispatch(setPageNumber(1));
  };

  const handleFilter = (temperament) => {
    dispatch(filterByTemperament(temperament));
    dispatch(setPageNumber(1));
  };

  return (
    <div className="container">
      <div className="row mt-4">
        <div className="col-md-4">
          <SearchBar handleSearch={handleSearch} />
        </div>
        <div className="col-md-4">
          <FilterDropDown temperaments={temperaments} handleFilter={handleFilter} />
        </div>
      </div>
      <div className="row mt-4">
        {currentDogs.map((dog) => (
          <div className="col-md-4" key={dog.id}>
            <DogCard dog={dog} />
          </div>
        ))}
      </div>
      <div className="row mt-4">
        <div className="col-md-12 d-flex justify-content-center">
          <Pagination dogsPerPage={dogsPerPage} totalDogs={totalDogs} paginate={paginate} />
        </div>
      </div>
    </div>
  );
};

export default HomePage;
