import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { findBreedsByName, clearSearch } from "../../redux/actions/index";
import { useHistory } from "react-router-dom";

const SearchBar = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const [searchTerm, setSearchTerm] = useState("");
  const filteredBreeds = useSelector((state) => state.filteredBreeds);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (searchTerm.trim() === "") {
      dispatch(clearSearch());
      history.push("/home");
    } else {
      dispatch(findBreedsByName(searchTerm));
      history.push(`dogs/name?name=${searchTerm}`);
    }
    setSearchTerm("");
  };

  const handleClearSearch = () => {
    dispatch(clearSearch());
  };

  useEffect(() => {
    if (history.location.pathname.includes("/dog/")) {
      dispatch(clearSearch());
    }
  }, [dispatch, history]);

  return (
    <div>
      <form onSubmit={handleSubmit} className="search-bar">
        <input
          type="text"
          placeholder="Search breeds by name"
          value={searchTerm}
          onChange={handleInputChange}
        />
        <button type="submit">Search</button>
      </form>
      {filteredBreeds.length > 0 && (
        <button onClick={handleClearSearch}>Clear Search</button>
      )}
    </div>
  );
};

export default SearchBar;
