import React from 'react';
import { useDispatch } from 'react-redux';
import { filterDogs, sortDogs } from '../../redux/actions/index';
import "./filters.css"

const Filters = () => {
const [temperament, setTemperament] = useState("");
const [origin, setOrigin] = useState("");
const [sortType, setSortType] = useState("");
const dispatch = useDispatch();

const handleFilterSubmit = (e) => {
e.preventDefault();
dispatch(filterDogs({ temperament, origin }));
};

const handleSortChange = (e) => {
setSortType(e.target.value);
dispatch(sortDogs(e.target.value));
};

return (
<div className="filters">
<form onSubmit={handleFilterSubmit}>
<label htmlFor="temperament-filter">Filtrar por temperamento:</label>
<input
id="temperament-filter"
type="text"
value={temperament}
onChange={(e) => setTemperament(e.target.value)}
/>
<label htmlFor="origin-filter">Filtrar por origen:</label>
<select
id="origin-filter"
value={origin}
onChange={(e) => setOrigin(e.target.value)}
>
<option value="">Todos</option>
<option value="api">API</option>
<option value="database">Base de datos</option>
</select>
<button type="submit">Filtrar</button>
</form>
<div className="sort">
<label htmlFor="sort-select">Ordenar por:</label>
<select
       id="sort-select"
       value={sortType}
       onChange={handleSortChange}
     >
<option value="">Seleccionar</option>
<option value="nameAsc">Nombre (A-Z)</option>
<option value="nameDesc">Nombre (Z-A)</option>
<option value="weightAsc">Peso (menor a mayor)</option>
<option value="weightDesc">Peso (mayor a menor)</option>
</select>
</div>
</div>
);
};

export default Filters;
