import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { findBreeds } from "../../redux/actions";

const SearchBar = () => {
  const dispatch = useDispatch();
  const [searchTerm, setSearchTerm] = useState("");
  const failed = useSelector((state) => state.findFailed);
  const [error, setError] = useState(null);

  useEffect(() => {
    setError(null);
  }, [dispatch]);

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    dispatch(findBreeds(searchTerm.trim()));
    if (failed) {
      setError(null);
    } else {
      setError("No hay perros");
    }
    setSearchTerm("");
  };

  const handleClear = () => {
    setSearchTerm("");
  };

  return (
    <div className="searchBar-container">
      <input
        type="text"
        placeholder="Search by name..."
        value={searchTerm}
        onChange={handleChange}
      />

      {searchTerm && (
        <button type="reset" className="clear-button" onClick={handleClear}>
          x
        </button>
      )}

      <button className="search-button" onClick={handleSearch}>
        Search
      </button>
      <div className="notsearch-found">{!failed && <h2>{error}</h2>}</div>
    </div>
  );
};

export default SearchBar;
