import React from "react";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { getBreedDetail } from "../../redux/actions/index";


const DogCard = ({ breed }) => {
  const dispatch = useDispatch();
  const { id, name, image, temperament, weight } = breed

  const handleDetailClick = () => {
    dispatch(getBreedDetail(id));
  };

  return (
    <div className="dog-card">
      <div className="dog-card-image-container">
        <img className="dog-card-image" src={image} alt={name} />
      </div>
      <div className="dog-card-info-container">
        <h2 className="dog-card-name">{name}</h2>
        <p className="dog-card-temperament">{temperament}</p>
        <p className="dog-card-weight">{weight} kg</p>
        <Link to={`/dog/${id}`}>
          <button onClick={handleDetailClick} className="dog-card-button">
            Ver Detalle
          </button>
        </Link>
      </div>
    </div>
  );
};

export default DogCard;
