import {
  GET_DOGS_SUCCESS,
  GET_DOGS_FAILURE,
  GET_DOG_DETAIL_SUCCESS,
  GET_DOG_DETAIL_FAILURE,
  FILTER_DOGS,
  SORT_DOGS,
  SET_ORIGIN_FILTER,
  SET_TEMPERAMENT_FILTER,
  SET_PAGE_NUMBER,
} from "../actions/index";

const initialState = {
  dogs: [],
  dogDetail: null,
  originFilter: "all",
  temperamentFilter: "all",
  sortOption: "asc",
  currentPage: 1,
  dogsPerPage: 8,
  error: null,
};

const dogsReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_DOGS_SUCCESS:
      return {
        ...state,
        dogs: action.payload,
        error: null,
      };
    case GET_DOGS_FAILURE:
      return {
        ...state,
        dogs: [],
        error: action.payload,
      };
    case GET_DOG_DETAIL_SUCCESS:
      return {
        ...state,
        dogDetail: action.payload,
        error: null,
      };
    case GET_DOG_DETAIL_FAILURE:
      return {
        ...state,
        dogDetail: null,
        error: action.payload,
      };
    case FILTER_DOGS:
      return {
        ...state,
        originFilter: action.payload.originFilter,
        temperamentFilter: action.payload.temperamentFilter,
        currentPage: 1,
      };
    case SORT_DOGS:
      return {
        ...state,
        sortOption: action.payload,
        currentPage: 1,
      };
    case SET_ORIGIN_FILTER:
      return {
        ...state,
        originFilter: action.payload,
        currentPage: 1,
      };
    case SET_TEMPERAMENT_FILTER:
      return {
        ...state,
        temperamentFilter: action.payload,
        currentPage: 1,
      };
    case SET_PAGE_NUMBER:
      return {
        ...state,
        currentPage: action.payload,
      };
    default:
      return state;
  }
};

export default dogsReducer;
