import React from "react";
import { connect } from "react-redux";
import { filterByTemperament, filterByOrigin } from "../../redux/actions";
import "./filterDropDown.css";

const FilterDropDown = (props) => {
  const handleSelect = (event) => {
    const { name, value } = event.target;

    if (name === "temperament") {
      props.filterByTemperament(value);
    } else if (name === "origin") {
      props.filterByOrigin(value);
    }
  };

  return (
    <div className="filter-dropdown-container">
      <div className="filter-by-temperament">
        <label htmlFor="temperament">Filter by temperament: </label>
        <select name="temperament" onChange={handleSelect}>
          <option value="">All</option>
          {props.temperaments.map((temperament, index) => (
            <option key={index} value={temperament.name}>
              {temperament.name}
            </option>
          ))}
        </select>
      </div>
      <div className="filter-by-origin">
        <label htmlFor="origin">Filter by origin: </label>
        <select name="origin" onChange={handleSelect}>
          <option value="">All</option>
          <option value="API">API</option>
          <option value="Database">Database</option>
        </select>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  temperaments: state.temperaments,
});

const mapDispatchToProps = {
  filterByTemperament,
  filterByOrigin,
};

export default connect(mapStateToProps, mapDispatchToProps)(FilterDropDown);
