//     pageNumbers.push(i);
//   }

//   const handleClick = (e) => {
//     setPageNumber(Number(e.target.id));
//   };

  return (
    <div className="pagination">
      <div className="page-buttons">
        {createPageButtons()}
      </div>
    </div>
  );
};

export default Pagination;
