import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { findBreedsByName } from "../../redux/actions";

const SearchBar = () => {
  const dispatch = useDispatch();
  const searchedBreeds = useSelector((state) => state.searchResults);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResult, setSearchResult] = useState([]);

  useEffect(() => {
    dispatch(findBreedsByName());
  }, [dispatch]);

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    const result = await dispatch(findBreedsByName(searchTerm.trim()));
    // searchResult = result;
    searchResult.push(result);
    // if (searchedBreeds.length === 0) {
    //   setSearchResult(true);
    //   setSearchTerm("Si entra");
    // } else {
    //   setSearchTerm("No entra");
    // }
    // setSearchTerm("");
    console.log(searchResult);
  };

  const handleClear = () => {
    setSearchTerm("");
  };

  return (
    <div className="searchBar-container">
      <form className="form-search" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={handleChange}
        />
        {searchTerm && (
          <button type="reset" className="clear-button" onClick={handleClear}>
            x
          </button>
        )}
        <button type="submit" className="search-button">
          Search
        </button>
      </form>
      {/* <div>{searchResult && <h2>Sin resultados</h2>}</div> */}
    </div>
  );
};

export default SearchBar;
