import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  loadDogs,
  searchDogsByName,
  filterDogs,
  sortDogs,
  setSort,
  getDogDetail,
  changePage
} from '../actions/dogsActions';
import DogsList from './DogsList';
import SearchBar from './SearchBar';
import Filters from './Filters';
import SortBy from './SortBy';
import Pagination from './Pagination';

const HomePage = () => {
  const dispatch = useDispatch();

  const [currentPage, setCurrentPage] = useState(1);
  const [filterParams, setFilterParams] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const [sortOption, setSortOption] = useState({ field: '', direction: '' });

  const dogs = useSelector((state) => state.dogs.dogs);
  const totalDogs = useSelector((state) => state.dogs.totalDogs);
  const isLoading = useSelector((state) => state.dogs.isLoading);
  const error = useSelector((state) => state.dogs.error);

  useEffect(() => {
    dispatch(loadDogs());
  }, [dispatch]);
  const handleSearch = (query) => {
    setSearchQuery(query);
    setCurrentPage(1);
    dispatch(searchDogsByName(query));
  };

  const handleFilter = (params) => {
    setFilterParams(params);
    setCurrentPage(1);
    dispatch(filterDogs(params));
  };

  const handleSort = (field, direction) => {
    setSortOption({ field, direction });
    dispatch(sortDogs(field, direction));
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
    dispatch(changePage(pageNumber));
  };

  const handleDogClick = (dogId) => {
    dispatch(getDogDetail(dogId));
  };

  return (
    <div className="homepage-container">
      <div className="homepage-header">
        <SearchBar onSearch={handleSearch} />
        <Filters onFilter={handleFilter} />
      </div>
      <div className="homepage-content">
        <div className="homepage-sidebar">
          <SortBy onSort={handleSort} />
        </div>
        <div className="homepage-main">
          {isLoading && <p>Loading dogs...</p>}
          {error && <p>{error}</p>}
          {!isLoading && !error && (
            <>
              <DogsList dogs={dogs} onDogClick={handleDogClick} />
              <Pagination
                currentPage={currentPage}
                totalPages={Math.ceil(totalDogs / 8)}
                onPageChange={handlePageChange}
              />
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
