import { GET_DOGS, GET_TEMPERAMENTS } from "../../redux/actions";

const initialState = {
  dogs: [],
  temperaments: [],
  currentPage: 1,
  dogsPerPage: 8,
  totalDogs: 0,
  search: "",
  temperamentFilter: "",
  originFilter: "",
  sortBy: "name",
  orderBy: "asc",
  dogDetail: null,
  error: null,
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_DOGS:
      return {
        ...state,
        dogs: action.payload.dogs,
        totalDogs: action.payload.totalDogs,
        currentPage: action.payload.currentPage,
        error: null,
      };
    case GET_TEMPERAMENTS:
      return {
        ...state,
        temperaments: action.payload,
        error: null,
      };
    case SET_CURRENT_PAGE:
      return {
        ...state,
        currentPage: action.payload,
      };
    case SET_SEARCH:
      return {
        ...state,
        search: action.payload,
      };
    case SET_TEMPERAMENT_FILTER:
      return {
        ...state,
        temperamentFilter: action.payload,
      };
    case SET_ORIGIN_FILTER:
      return {
        ...state,
        originFilter: action.payload,
      };
    case SET_SORT_BY:
      return {
        ...state,
        sortBy: action.payload,
      };
    case SET_ORDER_BY:
      return {
        ...state,
        orderBy: action.payload,
      };
    case GET_DOG_DETAIL:
      return {
        ...state,
        dogDetail: action.payload,
        error: null,
      };
    case CLEAR_DOG_DETAIL:
      return {
        ...state,
        dogDetail: null,
      };
    default:
      return state;
  }
};

export default rootReducer;
