import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { findBreedsByName } from "../../redux/actions";

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchError, setSearchError] = useState(false);
  const dispatch = useDispatch();

  const handleChange = (e) => {
    setSearchError(false);
    setSearchTerm(e.target.value);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    const search = await dispatch(findBreedsByName(searchTerm.trim()));

    if (!search) {
      setSearchError(true);
    } else {
      setSearchError(false);
    }
    setSearchTerm("");
  };

  const handleClear = () => {
    setSearchError(false);
    setSearchTerm("");
  };

  return (
    <div className="searchBar-container">
      <form className="form-search" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={handleChange}
        />
        {searchTerm && (
          <button type="reset" className="clear-button" onClick={handleClear}>
            x
          </button>
        )}
        <button type="submit" className="search-button">
          Search
        </button>
      </form>
      {searchError && (
        <h2>No se encontro ninguna raza de perro con ese nombre</h2>
      )}
    </div>
  );
};

export default SearchBar;
