import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { changePage, clearSearch, findBreedsByName } from "../../redux/actions";
import DogCard from "../dogCard/DogCard";
import Pagination from "../pagination/Pagination";
import Loading from "../loading/Loading";

const DogsList = () => {
  const dispatch = useDispatch();
  const breeds = useSelector((state) => state.filteredBreeds);
  const searchResults = useSelector((state) => state.searchResults);
  const currentPage = useSelector((state) => state.currentPage);
  const itemsPerPage = useSelector((state) => state.breedsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems =
    searchResults.length > 0
      ? searchResults.slice(indexOfFirstItem, indexOfLastItem)
      : breeds.slice(indexOfFirstItem, indexOfLastItem);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    dispatch(clearSearch());
    dispatch(changePage(1));
  }, [dispatch]);

  const handleSearch = () => {
    if (searchTerm) {
      dispatch(findBreedsByName(searchTerm));
    } else {
      dispatch(clearSearch());
    }
  };

  const handleInputChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <>
      <div className="search-bar-container">
        <input
          type="text"
          placeholder="Search by breed name"
          value={searchTerm}
          onChange={handleInputChange}
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={() => setSearchTerm("")}>Clear</button>
      </div>
      <div className="dogs-list-container">
        {currentItems.length > 0 ? (
          currentItems.map((breed) => <DogCard key={breed.id} breed={breed} />)
        ) : (
          <Loading />
        )}
      </div>
      {searchResults.length === 0 && breeds.length > 0 && (
        <Pagination itemsPerPage={itemsPerPage} totalItems={breeds.length} />
      )}
      {searchResults.length > 0 && (
        <Pagination
          itemsPerPage={itemsPerPage}
          totalItems={searchResults.length}
        />
      )}
    </>
  );
};

export default DogsList;
