import React from "react";
import { Link } from "react-router-dom";
import "./dogCard.css";

const DogCard = ({ dog }) => {
const { name, image, temperament, weight, id } = dog;

return (
<div className="card">
<Link to={`http://localhost:3001/dogs/${id}`} className="card-link">
<img src={image} alt={`Foto de ${name}`} className="card-img" />
<div className="card-body">
<h5 className="card-title">{name}</h5>
<p className="card-text">
<strong>Temperamentos:</strong> {temperament}
</p>
<p className="card-text">
<strong>Peso:</strong> {weight.metric} kg
</p>
</div>
</Link>
</div>
);
};

export default DogCard;
