import {
  GET_BREEDS,
  GET_BREED_DETAIL,
  GET_TEMPERAMENTS,
  FILTER_BY_TEMPERAMENT,
  FILTER_BY_ORIGIN,
  FILTER_BY_TEMPERAMENT_CLEAR,
  FILTER_BY_ORIGIN_CLEAR,
  SORT_BREEDS_BY_NAME,
  SORT_BREEDS_BY_WEIGHT,
  CHANGE_PAGE,
  CLEAR_FILTERS,
  CLEAR_SEARCH,
} from "../../redux/actions/index";

const initialState = {
  breeds: [],
  breedDetail: {},
  temperaments: [],
  filteredBreeds: [],
  filterByTemperament: null,
  filterByOrigin: null,
  sort: "",
  page: 1,
  searchResults: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_BREEDS:
      return {
        ...state,
        breeds: action.payload,
        filteredBreeds: action.payload,
      };
    case GET_BREED_DETAIL:
      return {
        ...state,
        breedDetail: action.payload,
      };
    case GET_TEMPERAMENTS:
      return {
        ...state,
        temperaments: action.payload,
      };
    case FILTER_BY_TEMPERAMENT:
      const filteredBreedsByTemperament =
        action.payload === "All"
          ? state.breeds
          : state.breeds.filter((breed) =>
              breed.temperament?.includes(action.payload)
            );
      return {
        ...state,
        filteredBreeds: filteredBreedsByTemperament,
        filterByTemperament: action.payload,
        page: 1,
      };
    case FILTER_BY_ORIGIN:
      const filteredBreedsByOrigin =
        action.payload === "All"
          ? state.breeds
          : state.breeds.filter((breed) => breed.origin === action.payload);
      return {
        ...state,
        filteredBreeds: filteredBreedsByOrigin,
        filterByOrigin: action.payload,
        page: 1,
      };
    case FILTER_BY_TEMPERAMENT_CLEAR:
      return {
        ...state,
        filteredBreeds: state.breeds,
        filterByTemperament: action.payload,
        page: 1,
      };
    case FILTER_BY_ORIGIN_CLEAR:
      return {
        ...state,
        filteredBreeds: state.breeds,
        filterByOrigin: action.payload,
        page: 1,
      };
    case SORT_BREEDS_BY_NAME:
      return {
        ...state,
        filteredBreeds: action.payload,
        sort: "name",
      };
    case SORT_BREEDS_BY_WEIGHT:
      return {
        ...state,
        filteredBreeds: action.payload,
        sort: "weight",
      };
    case CHANGE_PAGE:
      return {
        ...state,
        page: action.payload,
      };
    case CLEAR_FILTERS:
      return {
        ...state,
        filterByTemperament: null,
        filterByOrigin: null,
        filteredBreeds: state.breeds,
        page: 1,
      };
    case CLEAR_SEARCH:
      return {
        ...state,
        searchResults: [],
        page: 1,
      };
    default:
      return state;
  }
};

export default rootReducer;
