import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { changePage } from "../../redux/actions/index";

const Pagination = () => {
  const dispatch = useDispatch();
  const currentPage = useSelector((state) => state.currentPage);
  const breedsPerPage = useSelector((state) => state.breedsPerPage);
  const searchedBreeds = useSelector((state) => state.searchResults);
  const filteredBreeds = useSelector((state) => state.filteredBreeds);
  const breeds = useSelector((state) =>
    searchedBreeds.length > 0
      ? searchedBreeds
      : filteredBreeds.length > 0
      ? filteredBreeds
      : state.allBreeds
  );

  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(breeds / breedsPerPage); i++) {
    pageNumbers.push(i);
  }

  const handleClick = (number) => {
    dispatch(changePage(number));
  };

  return (
    <nav>
      <div className="pagination">
        <ul>
          {pageNumbers.map((number) => (
            <li
              key={number}
              onClick={() => handleClick(number)}
              className={currentPage === number ? "active" : ""}
            >
              {number}
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
};

export default Pagination;
