import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getBreedDetail } from "../../redux/actions/index";
import Header from "../header/Header";
import Loading from "../loading/Loading";

const TemperamentList = ({ temperaments }) => {
  if (!Array.isArray(temperaments)) {
    return null;
  }
  return (
    <>
      {temperaments.map((temp) => (
        <li key={temp}>{temp}</li>
      ))}
    </>
  );
};

const DogDetail = (props) => {
  const dispatch = useDispatch();
  const breed = useSelector(
    (state) => state.breedDetail || location.state.breed
  );
  const loading = useSelector((state) => state.loading);
  const { id } = props.match.params;

  useEffect(() => {
    dispatch(getBreedDetail(match.params.id));
  }, [dispatch, match.params.id]);

  if (loading) {
    return <Loading />;
  }

  if (!breed) {
    return <h2>Breed not found</h2>;
  }

  return (
    <div className="breeddetail-container">
      <div>
        <Header></Header>
      </div>
      <div className="breed-detail">
        <div className="breed-image">
          <img src={breed.image} alt={breed.name} />
        </div>
        <div className="breed-info">
          <h2>{breed.name}</h2>
          <p>{breed.temperament}</p>
          <p>
            <strong>Height:</strong> {breed.height} cm
          </p>
          <p>
            <strong>Weight:</strong> {breed.weight} kg
          </p>
          <p>
            <strong>Lifespan:</strong> {breed.life_span}
          </p>
          <div>
            <strong>Temperaments:</strong>
            <TemperamentList key={breed.id} temperaments={breed.temperaments} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default DogDetail;
