import React, { useState, useEffect } from 'react';
// import { useSelector, useDispatch } from "react-redux";
import { connect } from 'react-redux';
import { searchDogsByName, loadDogs, filterDogs, sortDogs, setSort, changePage } from '../../redux/actions/index';
import SearchBar from '../searchBar/SearchBar';
import DogCard from '../dogCard/DogCard';
// import Filters from '../filters/Filters';
import Pagination from '../pagination/Pagination';
import "./homePage.css"


const HomePage = ({
  dogs,
  loading,
  error,
  searchDogsByName,
  loadDogs,
  filterDogs,
  sortDogs,
  setSort,
  changePage,
}) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterTerm, setFilterTerm] = useState("");
  const [sortType, setSortType] = useState("");

  useEffect(() => {
    // Cargar los primeros resultados de razas de perros al cargar la página
    loadDogs();
  }, [loadDogs]);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    searchDogsByName(event.target.value);
  };

  const handleFilter = (event) => {
    setFilterTerm(event.target.value);
    filterDogs(event.target.value);
  };

  const handleSort = (event) => {
    setSortType(event.target.value);
    sortDogs(event.target.value);
  };

  const handlePageChange = (pageNumber) => {
    changePage(pageNumber);
  };

  return (
    <div>
      <h1>Home Page</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <SearchBar
            searchTerm={searchTerm}
            handleSearch={handleSearch}
            filterTerm={filterTerm}
            handleFilter={handleFilter}
            sortType={sortType}
            handleSort={handleSort}
          />
          <div className="dog-card-container">
            {dogs.map(dog => (
              <DogCard key={dog.id} dogs={dog} />
            ))}
          </div>
          <Pagination
            currentPage={dogs.currentPage}
            totalPages={dogs.totalPages}
            handlePageChange={handlePageChange}
          />
        </div>
      )}
    </div>
  );
};

const mapStateToProps = (state) => ({
  dogs: state.dogs,
  loading: state.loading,
  error: state.error,
});

export default connect(mapStateToProps, {
  searchDogsByName,
  loadDogs,
  filterDogs,
  sortDogs,
  setSort,
  changePage,
})(HomePage);
