import {
  SEARCH_DOGS_BY_NAME,
  LOAD_DOGS,
  FILTER_DOGS,
  SET_SORT,
  SORT_DOGS,
  GET_DOG_DETAIL,
  CHANGE_PAGE,
} from "../actions/index";

const initialState = {
  dogs: [], // Listado de razas de perros
  filteredDogs: [], // Listado de razas de perros filtradas
  dogDetail: null, // Detalle de una raza de perro específica
  searchQuery: "", // Consulta de búsqueda por nombre de raza de perro
  filterBy: "", // Filtro aplicado (temperamento, origen)
  sortBy: "", // Tipo de clasificación (alfabético, peso)
  sortOrder: "", // Orden de clasificación (ascendente, descendente)
  currentPage: 1, // Página actual del paginado
  dogsPerPage: 8, // Cantidad de perros por página
};

const dogsReducer = (state = initialState, action) => {
  switch (action.type) {
    case SEARCH_DOGS_BY_NAME:
      return {
        ...state,
        searchQuery: action.payload,
        currentPage: 1, // Reiniciar la página al realizar una búsqueda
      };
    case LOAD_DOGS:
      return {
        ...state,
        dogs: action.payload,
        filteredDogs: action.payload,
      };
    case FILTER_DOGS:
      const { filterBy, filterValue } = action.payload;
      let filteredDogs = state.dogs.filter((dog) => {
        return dog[filterBy].includes(filterValue);
      });
      return {
        ...state,
        filteredDogs,
        filterBy,
      };
    case SORT_DOGS:
      const { sortBy, sortOrder } = action.payload;
      let sortedDogs = [...state.filteredDogs];
      if (sortBy === "name") {
        sortedDogs.sort((a, b) => {
          const nameA = a.name.toUpperCase();
          const nameB = b.name.toUpperCase();
          if (sortOrder === "asc") {
            return nameA < nameB ? -1 : nameA > nameB ? 1 : 0;
          } else {
            return nameA > nameB ? -1 : nameA < nameB ? 1 : 0;
          }
        });
      } else if (sortBy === "weight") {
        sortedDogs.sort((a, b) => {
          const weightA = a.weight.metric;
          const weightB = b.weight.metric;
          if (sortOrder === "asc") {
            return weightA - weightB;
          } else {
            return weightB - weightA;
          }
        });
      }
      return {
        ...state,
        filteredDogs: sortedDogs,
        sortBy,
        sortOrder,
      };
    case SET_SORT:
      return {
        ...state,
        sortBy: action.payload,
      };
    case GET_DOG_DETAIL:
      return {
        ...state,
        dogDetail: action.payload,
      };
    case CHANGE_PAGE:
      return {
        ...state,
        currentPage: action.payload,
      };
    default:
      return state;
  }
};

export default dogsReducer;
