import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { changePage } from "../../redux/actions/index";

const Pagination = () => {
  const dispatch = useDispatch();
  const currentPage = useSelector((state) => state.currentPage);
  const breedsPerPage = useSelector((state) => state.breedsPerPage);
  const totalBreeds = useSelector((state) => state.filteredBreeds.length);
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalBreeds / breedsPerPage); i++) {
    pageNumbers.push(i);
  }

  const handleClick = (pageNumber) => {
    dispatch(changePage(pageNumber));
    console.log(pageNumber);
  };

  return (
    <nav>
      <ul className="pagination">
        {Array(Math.ceil(totalBreeds / breedsPerPage))
          .fill()
          .map((_, index) => (
            <li
              key={index}
              className={`page-item ${currentPage === index ? "active" : ""}`}
            >
              <button onClick={() => handleClick(index)} className="page-link">
                {index}
              </button>
            </li>
          ))}
      </ul>
    </nav>
  );
};

export default Pagination;
