import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { findBreedsByName } from "../../redux/actions";

const SearchBar = () => {
  const dispatch = useDispatch();
  const [searchTerm, setSearchTerm] = useState("");
  const [error, setError] = useState(false);

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    dispatch(findBreedsByName(searchTerm.trim())).catch(() => setError(true));
    setSearchTerm("");
  };

  const handleClear = () => {
    setSearchTerm("");
  };

  return (
    <div className="searchBar-container">
      <form className="form-search" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={handleChange}
        />

        {searchTerm && (
          <button type="reset" className="clear-button" onClick={handleClear}>
            x
          </button>
        )}

        <button type="submit" className="search-button">
          Search
        </button>
      </form>

      {error && <h2>Error al buscar perros. Inténtelo de nuevo más tarde.</h2>}
    </div>
  );
};

export default SearchBar;
