import {
  SEARCH_DOGS_BY_NAME,
  LOAD_DOGS,
  FILTER_DOGS,
  SORT_DOGS,
  GET_DOG_DETAIL,
  CHANGE_PAGE,
} from "../actions/index";

const initialState = {
  dogs: [], // Agregar la propiedad "dogs" al estado inicial
  filteredDogs: [],
  dogDetail: null,
  currentPage: 1,
  dogsPerPage: 8,
};

const dogsReducer = (state = initialState, action) => {
  switch (action.type) {
    case SEARCH_DOGS_BY_NAME:
      return {
        ...state,
        filteredDogs: action.payload,
        dogs: action.payload, // Actualizar la propiedad "dogs"
        currentPage: 1,
      };
    case LOAD_DOGS:
      return {
        ...state,
        dogs: action.payload, // Actualizar la propiedad "dogs"
        filteredDogs: action.payload,
      };
    case FILTER_DOGS:
      return {
        ...state,
        filteredDogs: action.payload,
        dogs: action.payload, // Actualizar la propiedad "dogs"
        currentPage: 1,
      };
    case SORT_DOGS:
      return {
        ...state,
        filteredDogs: action.payload,
        dogs: action.payload, // Actualizar la propiedad "dogs"
        currentPage: 1,
      };
    case GET_DOG_DETAIL:
      return {
        ...state,
        dogDetail: action.payload,
      };
    case CHANGE_PAGE:
      return {
        ...state,
        currentPage: action.payload,
      };
    default:
      return state;
  }
};

export default dogsReducer;
